+++ 
@@ -0,0 +1,1231 @@
+<!DOCTYPE html>
+<html lang="en">
+<head>
+    <meta charset="UTF-8">
+    <meta name="viewport" content="width=device-width, initial-scale=1.0">
+    <title>Nexus Tech Digital Solutions - Request Your Project</title>
+    <style>
+        * {
+            margin: 0;
+            padding: 0;
+            box-sizing: border-box;
+        }
+
+        body {
+            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
+            background: #000000;
+            color: #ffffff;
+            overflow-x: hidden;
+            cursor: none;
+        }
+
+        /* Custom Cursor */
+        .cursor {
+            position: fixed;
+            width: 20px;
+            height: 20px;
+            background: linear-gradient(45deg, #00d4ff, #0066ff);
+            border-radius: 50%;
+            pointer-events: none;
+            z-index: 9999;
+            transition: transform 0.1s ease;
+            mix-blend-mode: difference;
+        }
+
+        .cursor.hover {
+            transform: scale(2);
+        }
+
+        /* PlayStation Style Header */
+        .header {
+            position: fixed;
+            top: 0;
+            width: 100%;
+            background: rgba(0, 0, 0, 0.95);
+            backdrop-filter: blur(20px);
+            z-index: 1000;
+            padding: 15px 0;
+            border-bottom: 1px solid rgba(0, 212, 255, 0.3);
+        }
+
+        .nav {
+            max-width: 1400px;
+            margin: 0 auto;
+            display: flex;
+            justify-content: space-between;
+            align-items: center;
+            padding: 0 30px;
+        }
+
+        .logo {
+            font-size: 28px;
+            font-weight: 900;
+            color: #00d4ff;
+            text-transform: uppercase;
+            letter-spacing: 2px;
+            text-shadow: 0 0 20px rgba(0, 212, 255, 0.5);
+        }
+
+        .nav-status {
+            display: flex;
+            align-items: center;
+            gap: 20px;
+            font-size: 14px;
+            color: #8b949e;
+            text-transform: uppercase;
+            letter-spacing: 1px;
+        }
+
+        .status-indicator {
+            width: 12px;
+            height: 12px;
+            background: #00d4ff;
+            border-radius: 50%;
+            animation: pulse 2s infinite;
+            box-shadow: 0 0 10px rgba(0, 212, 255, 0.8);
+        }
+
+        @keyframes pulse {
+            0%, 100% { opacity: 1; transform: scale(1); }
+            50% { opacity: 0.5; transform: scale(1.2); }
+        }
+
+        /* Main Container */
+        .main-container {
+            height: 100vh;
+            display: flex;
+            position: relative;
+            overflow: hidden;
+            padding-top: 90px;
+        }
+
+        /* Left Panel - Project Showcase */
+        .project-panel {
+            flex: 1;
+            position: relative;
+            background: linear-gradient(135deg, #0d1117 0%, #161b22 100%);
+            overflow: hidden;
+        }
+
+        .project-carousel {
+            position: relative;
+            width: 100%;
+            height: 100%;
+        }
+
+        .project-slide {
+            position: absolute;
+            top: 0;
+            left: 0;
+            width: 100%;
+            height: 100%;
+            opacity: 0;
+            transition: opacity 1s ease-in-out;
+            background-size: cover;
+            background-position: center;
+            display: flex;
+            align-items: flex-end;
+            padding: 60px;
+        }
+
+        .project-slide.active {
+            opacity: 1;
+        }
+
+        .project-slide::before {
+            content: '';
+            position: absolute;
+            top: 0;
+            left: 0;
+            right: 0;
+            bottom: 0;
+            background: linear-gradient(
+                135deg,
+                rgba(13, 17, 23, 0.7) 0%,
+                rgba(0, 102, 255, 0.3) 50%,
+                rgba(0, 212, 255, 0.2) 100%
+            );
+            z-index: 1;
+        }
+
+        .project-info {
+            position: relative;
+            z-index: 2;
+            max-width: 500px;
+        }
+
+        .project-category {
+            color: #00d4ff;
+            font-size: 14px;
+            font-weight: 700;
+            text-transform: uppercase;
+            letter-spacing: 2px;
+            margin-bottom: 10px;
+        }
+
+        .project-title {
+            font-size: 3rem;
+            font-weight: 900;
+            text-transform: uppercase;
+            letter-spacing: -1px;
+            margin-bottom: 20px;
+            text-shadow: 2px 2px 20px rgba(0, 0, 0, 0.8);
+        }
+
+        .project-description {
+            font-size: 1.1rem;
+            line-height: 1.6;
+            color: rgba(255, 255, 255, 0.9);
+            margin-bottom: 30px;
+        }
+
+        .project-tech {
+            display: flex;
+            gap: 15px;
+            flex-wrap: wrap;
+        }
+
+        .tech-tag {
+            background: rgba(0, 212, 255, 0.2);
+            border: 1px solid rgba(0, 212, 255, 0.5);
+            color: #00d4ff;
+            padding: 8px 16px;
+            border-radius: 20px;
+            font-size: 12px;
+            font-weight: 600;
+            text-transform: uppercase;
+            letter-spacing: 1px;
+        }
+
+        /* Right Panel - Waitlist */
+        .waitlist-panel {
+            width: 500px;
+            background: linear-gradient(135deg, #000000 0%, #0d1117 100%);
+            display: flex;
+            flex-direction: column;
+            justify-content: center;
+            align-items: center;
+            padding: 60px 40px;
+            position: relative;
+            border-left: 1px solid rgba(0, 212, 255, 0.3);
+            overflow-y: auto;
+        }
+
+        .waitlist-panel::before {
+            content: '';
+            position: absolute;
+            top: 0;
+            left: 0;
+            right: 0;
+            bottom: 0;
+            background: 
+                radial-gradient(circle at 50% 20%, rgba(0, 212, 255, 0.1) 0%, transparent 50%),
+                radial-gradient(circle at 80% 80%, rgba(0, 102, 255, 0.1) 0%, transparent 50%);
+            z-index: 1;
+        }
+
+        .waitlist-content {
+            position: relative;
+            z-index: 2;
+            text-align: center;
+            width: 100%;
+        }
+
+        .launch-badge {
+            background: linear-gradient(45deg, #0066ff, #00d4ff);
+            color: white;
+            padding: 12px 24px;
+            border-radius: 25px;
+            font-size: 12px;
+            font-weight: 700;
+            text-transform: uppercase;
+            letter-spacing: 2px;
+            margin-bottom: 30px;
+            display: inline-block;
+            box-shadow: 0 10px 30px rgba(0, 102, 255, 0.3);
+        }
+
+        .waitlist-title {
+            font-size: 2.8rem;
+            font-weight: 900;
+            text-transform: uppercase;
+            letter-spacing: -1px;
+            margin-bottom: 20px;
+            background: linear-gradient(45deg, #ffffff, #00d4ff);
+            -webkit-background-clip: text;
+            -webkit-text-fill-color: transparent;
+            background-clip: text;
+        }
+
+        .waitlist-subtitle {
+            font-size: 1.2rem;
+            color: #8b949e;
+            margin-bottom: 40px;
+            line-height: 1.6;
+        }
+
+        .waitlist-form {
+            width: 100%;
+            margin-bottom: 40px;
+        }
+
+        .form-group {
+            margin-bottom: 20px;
+        }
+
+        .form-input {
+            width: 100%;
+            background: rgba(255, 255, 255, 0.05);
+            border: 1px solid rgba(0, 212, 255, 0.3);
+            border-radius: 8px;
+            padding: 16px 20px;
+            color: white;
+            font-size: 16px;
+            transition: all 0.3s ease;
+            font-family: inherit;
+        }
+
+        .form-input:focus {
+            outline: none;
+            border-color: #00d4ff;
+            box-shadow: 0 0 0 3px rgba(0, 212, 255, 0.2);
+            background: rgba(255, 255, 255, 0.08);
+        }
+
+        .form-input::placeholder {
+            color: rgba(255, 255, 255, 0.5);
+        }
+
+        .form-input option {
+            background: #161b22;
+            color: white;
+        }
+
+        .submit-btn {
+            width: 100%;
+            background: linear-gradient(45deg, #0066ff, #00d4ff);
+            border: none;
+            border-radius: 8px;
+            padding: 18px 24px;
+            color: white;
+            font-size: 16px;
+            font-weight: 700;
+            text-transform: uppercase;
+            letter-spacing: 1px;
+            cursor: pointer;
+            transition: all 0.3s ease;
+            box-shadow: 0 10px 30px rgba(0, 102, 255, 0.3);
+        }
+
+        .submit-btn:hover {
+            transform: translateY(-2px);
+            box-shadow: 0 15px 40px rgba(0, 102, 255, 0.4);
+        }
+
+        .submit-btn:active {
+            transform: translateY(0);
+        }
+
+        .waitlist-features {
+            list-style: none;
+            text-align: left;
+        }
+
+        .waitlist-features li {
+            padding: 8px 0;
+            color: #8b949e;
+            position: relative;
+            padding-left: 30px;
+        }
+
+        .waitlist-features li::before {
+            content: '▶';
+            position: absolute;
+            left: 0;
+            color: #00d4ff;
+            font-size: 12px;
+        }
+
+        /* Navigation Dots */
+        .carousel-nav {
+            position: absolute;
+            bottom: 30px;
+            right: 30px;
+            display: flex;
+            gap: 15px;
+            z-index: 3;
+        }
+
+        .nav-dot {
+            width: 12px;
+            height: 12px;
+            border-radius: 50%;
+            background: rgba(255, 255, 255, 0.3);
+            cursor: pointer;
+            transition: all 0.3s ease;
+        }
+
+        .nav-dot.active {
+            background: #00d4ff;
+            box-shadow: 0 0 15px rgba(0, 212, 255, 0.8);
+            transform: scale(1.2);
+        }
+
+        /* Progress Bar */
+        .progress-bar {
+            position: absolute;
+            bottom: 0;
+            left: 0;
+            height: 3px;
+            background: #00d4ff;
+            transition: width 5s linear;
+            z-index: 3;
+        }
+
+        /* Success Message */
+        .success-message {
+            display: none;
+            background: rgba(34, 197, 94, 0.1);
+            border: 1px solid rgba(34, 197, 94, 0.3);
+            color: #22c55e;
+            padding: 20px;
+            border-radius: 8px;
+            margin-top: 20px;
+            text-align: center;
+        }
+
+        /* Service Guide Styles */
+        .service-guide {
+            background: rgba(13, 17, 23, 0.9);
+            border: 1px solid rgba(0, 212, 255, 0.3);
+            border-radius: 12px;
+            padding: 30px;
+            margin-top: 20px;
+            max-height: 70vh;
+            overflow-y: auto;
+            text-align: left;
+        }
+
+        .service-item {
+            background: rgba(255, 255, 255, 0.05);
+            border: 1px solid rgba(0, 212, 255, 0.2);
+            border-radius: 8px;
+            padding: 20px;
+            margin-bottom: 20px;
+            transition: all 0.3s ease;
+        }
+
+        .service-item:hover {
+            border-color: rgba(0, 212, 255, 0.4);
+            transform: translateY(-2px);
+        }
+
+        .service-header {
+            display: flex;
+            justify-content: space-between;
+            align-items: flex-start;
+            margin-bottom: 10px;
+            flex-wrap: wrap;
+        }
+
+        .service-header h4 {
+            color: #00d4ff;
+            font-size: 1.1rem;
+            font-weight: 700;
+            margin: 0;
+            flex: 1;
+            min-width: 250px;
+        }
+
+        .service-price {
+            background: linear-gradient(45deg, #0066ff, #00d4ff);
+            color: white;
+            padding: 6px 12px;
+            border-radius: 20px;
+            font-weight: 700;
+            font-size: 0.9rem;
+            white-space: nowrap;
+        }
+
+        .service-meta {
+            display: flex;
+            gap: 20px;
+            margin-bottom: 12px;
+            flex-wrap: wrap;
+        }
+
+        .service-meta span {
+            color: #8b949e;
+            font-size: 0.85rem;
+        }
+
+        .service-perfect-for {
+            color: #c9d1d9;
+            font-size: 0.9rem;
+            margin-bottom: 15px;
+            line-height: 1.5;
+        }
+
+        .service-details {
+            display: grid;
+            grid-template-columns: 1fr 1fr;
+            gap: 20px;
+            margin-top: 15px;
+        }
+
+        .service-included, .service-benefits {
+            color: #c9d1d9;
+            font-size: 0.85rem;
+        }
+
+        .service-included strong, .service-benefits strong {
+            color: #ffffff;
+            display: block;
+            margin-bottom: 8px;
+        }
+
+        .service-included ul, .service-benefits ul {
+            list-style: none;
+            padding-left: 0;
+        }
+
+        .service-included li, .service-benefits li {
+            padding: 4px 0;
+            padding-left: 20px;
+            position: relative;
+        }
+
+        .service-included li::before {
+            content: '✓';
+            position: absolute;
+            left: 0;
+            color: #00d4ff;
+            font-weight: bold;
+        }
+
+        .service-benefits li::before {
+            content: '★';
+            position: absolute;
+            left: 0;
+            color: #ffa500;
+            font-weight: bold;
+        }
+
+        .service-guide-btn, .calendly-btn {
+            background: linear-gradient(45deg, #0066ff, #00d4ff);
+            color: white;
+            padding: 12px 24px;
+            border-radius: 6px;
+            border: none;
+            font-weight: 700;
+            text-transform: uppercase;
+            letter-spacing: 0.5px;
+            transition: all 0.3s ease;
+            cursor: pointer;
+            text-decoration: none;
+            display: inline-block;
+        }
+
+        .service-guide-btn:hover, .calendly-btn:hover {
+            transform: translateY(-2px);
+            box-shadow: 0 8px 20px rgba(0, 102, 255, 0.4);
+        }
+
+        /* Back button hover effect */
+        .service-guide button:hover {
+            background: rgba(255, 255, 255, 0.15) !important;
+            border-color: rgba(0, 212, 255, 0.5) !important;
+            transform: translateY(-1px);
+        }
+
+        /* Responsive Design */
+        @media (max-width: 768px) {
+            .main-container {
+                flex-direction: column;
+                height: auto;
+                min-height: 100vh;
+                padding-top: 60px;
+            }
+
+            .project-panel {
+                height: 60vh;
+            }
+
+            .waitlist-panel {
+                width: 100%;
+                padding: 40px 20px;
+                height: auto;
+                min-height: auto;
+            }
+
+            .project-slide {
+                padding: 30px 20px;
+            }
+
+            .project-title {
+                font-size: 2rem;
+            }
+
+            .waitlist-title {
+                font-size: 2rem;
+            }
+
+            .nav {
+                padding: 0 20px;
+            }
+
+            .cursor {
+                display: none;
+            }
+
+            body {
+                cursor: auto;
+            }
+
+            .service-guide {
+                padding: 20px;
+                max-height: 60vh;
+            }
+            
+            .service-item {
+                padding: 15px;
+            }
+            
+            .service-header {
+                flex-direction: column;
+                align-items: flex-start;
+            }
+            
+            .service-header h4 {
+                margin-bottom: 8px;
+                min-width: auto;
+            }
+            
+            .service-details {
+                grid-template-columns: 1fr;
+                gap: 15px;
+            }
+            
+            .service-meta {
+                flex-direction: column;
+                gap: 5px;
+            }
+        }
+
+        /* Gaming UI Elements */
+        .ui-corner {
+            position: absolute;
+            width: 30px;
+            height: 30px;
+            border: 2px solid rgba(0, 212, 255, 0.5);
+        }
+
+        .ui-corner.top-left {
+            top: 20px;
+            left: 20px;
+            border-right: none;
+            border-bottom: none;
+        }
+
+        .ui-corner.top-right {
+            top: 20px;
+            right: 20px;
+            border-left: none;
+            border-bottom: none;
+        }
+
+        .ui-corner.bottom-left {
+            bottom: 20px;
+            left: 20px;
+            border-right: none;
+            border-top: none;
+        }
+
+        .ui-corner.bottom-right {
+            bottom: 20px;
+            right: 20px;
+            border-left: none;
+            border-top: none;
+        }
+    </style>
+</head>
+<body>
+    <!-- Custom Cursor -->
+    <div class="cursor"></div>
+
+    <!-- Header -->
+    <header class="header">
+        <nav class="nav">
+            <div class="logo">Nexus Tech</div>
+            <div class="nav-status">
+                <div class="status-indicator"></div>
+                System Status: Online
+            </div>
+        </nav>
+    </header>
+
+    <!-- Main Container -->
+    <div class="main-container">
+        <!-- Gaming UI Corners -->
+        <div class="ui-corner top-left"></div>
+        <div class="ui-corner top-right"></div>
+        <div class="ui-corner bottom-left"></div>
+        <div class="ui-corner bottom-right"></div>
+
+        <!-- Project Showcase Panel -->
+        <div class="project-panel">
+            <div class="project-carousel">
+                <div class="progress-bar"></div>
+                
+                <!-- Slide 1 - Use your existing images OR fallback gradients -->
+                <div class="project-slide active" style="background-image: url('assets/image1.png'), linear-gradient(135deg, #667eea 0%, #764ba2 100%);">
+                    <div class="project-info">
+                        <div class="project-category">Action Adventure Game</div>
+                        <h2 class="project-title">NEXORA</h2>
+                        <p class="project-description">
+                            Immersive action-adventure experience featuring dynamic combat systems, 
+                            stunning visuals, and an epic storyline that redefines modern gaming.
+                        </p>
+                        <div class="project-tech">
+                            <span class="tech-tag">Unreal Engine</span>
+                            <span class="tech-tag">C++</span>
+                            <span class="tech-tag">3D Graphics</span>
+                            <span class="tech-tag">AI Systems</span>
+                        </div>
+                    </div>
+                </div>
+
+                <!-- Slide 2 -->
+                <div class="project-slide" style="background-image: url('assets/image2.PNG'), linear-gradient(135deg, #f093fb 0%, #f5576c 100%);">
+                    <div class="project-info">
+                        <div class="project-category">Combat System</div>
+                        <h2 class="project-title">NEXORA Combat</h2>
+                        <p class="project-description">
+                            Advanced tactical combat system with precision archery mechanics, 
+                            strategic gameplay elements, and immersive character progression.
+                        </p>
+                        <div class="project-tech">
+                            <span class="tech-tag">Physics Engine</span>
+                            <span class="tech-tag">Animation</span>
+                            <span class="tech-tag">Multiplayer</span>
+                            <span class="tech-tag">Real-time</span>
+                        </div>
+                    </div>
+                </div>
+
+                <!-- Slide 3 -->
+                <div class="project-slide" style="background-image: url('assets/'), linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);">
+                    <div class="project-info">
+                        <div class="project-category">Survival Adventure</div>
+                        <h2 class="project-title">Invisible</h2>
+                        <p class="project-description">
+                            Atmospheric survival adventure featuring Meishavernell in a mysterious world. 
+                            Stealth mechanics, environmental storytelling, and psychological thriller elements.
+                        </p>
+                        <div class="project-tech">
+                            <span class="tech-tag">Unity 3D</span>
+                            <span class="tech-tag">Procedural</span>
+                            <span class="tech-tag">VR Ready</span>
+                            <span class="tech-tag">Atmospheric</span>
+                        </div>
+                    </div>
+                </div>
+
+                <!-- Slide 4 -->
+                <div class="project-slide" style="background-image: linear-gradient(45deg, #fa709a, #fee140, #ff6b6b);">
+                    <div class="project-info">
+                        <div class="project-category">Full Portfolio</div>
+                        <h2 class="project-title">More Projects</h2>
+                        <p class="project-description">
+                            Explore our complete portfolio of games, web applications, mobile apps, 
+                            and digital solutions. From concept to launch, we bring visions to life.
+                        </p>
+                        <div class="project-tech">
+                            <span class="tech-tag">Web Development</span>
+                            <span class="tech-tag">Mobile Apps</span>
+                            <span class="tech-tag">Game Development</span>
+                            <span class="tech-tag">Digital Solutions</span>
+                        </div>
+                    </div>
+                </div>
+
+                <!-- Navigation Dots -->
+                <div class="carousel-nav">
+                    <div class="nav-dot active" data-slide="0"></div>
+                    <div class="nav-dot" data-slide="1"></div>
+                    <div class="nav-dot" data-slide="2"></div>
+                    <div class="nav-dot" data-slide="3"></div>
+                </div>
+            </div>
+        </div>
+
+        <!-- Waitlist Panel -->
+        <div class="waitlist-panel">
+            <div class="waitlist-content">
+                <div class="launch-badge">$25 Consultation</div>
+                <h1 class="waitlist-title">Request Your Project</h1>
+                <p class="waitlist-subtitle">
+                    Ready to bring your vision to life? Tell us about your project and book a strategic 45-minute consultation to discuss your digital solution.
+                </p>
+
+                <!-- NETLIFY FORM - Properly configured -->
+                <form class="waitlist-form" name="project-request" method="POST" data-netlify="true" data-netlify-honeypot="bot-field">
+                    <!-- Hidden fields for Netlify -->
+                    <input type="hidden" name="form-name" value="project-request" />
+                    <!-- Honeypot field to prevent spam (hidden from users) -->
+                    <p style="display: none;">
+                        <input name="bot-field" />
+                    </p>
+                    
+                    <div class="form-group">
+                        <input type="text" name="name" class="form-input" placeholder="Full Name" required />
+                    </div>
+                    
+                    <div class="form-group">
+                        <input type="email" name="email" class="form-input" placeholder="Email Address" required />
+                    </div>
+                    
+                    <div class="form-group">
+                        <input type="text" name="company" class="form-input" placeholder="Company Name" required />
+                    </div>
+
+                    <div class="form-group">
+                        <select name="project-type" class="form-input" required>
+                            <option value="">Select Project Type</option>
+                            <option value="website">Website Development</option>
